mapper.select:
SELECT SNT_TELNO
FROM PEG_SMS.TGWSM_CUST_INFO
WHERE CUST_CD = #{cust_id}

{CALL PEG_SMS.P2P_G02_APPR_NO(
		#{cust_id,		mode=IN,	jdbcType=VARCHAR,	javaType=java.lang.String},
		#{an_ret,		mode=OUT,	jdbcType=VARCHAR,	javaType=java.lang.String,	resultMap=tokenMap},
		#{an_dbcode,	mode=OUT,	jdbcType=INTEGER,	javaType=java.lang.Integer,	resultMap=tokenMap},
		#{as_dberrtext,	mode=OUT,	jdbcType=VARCHAR,	javaType=java.lang.String,	resultMap=tokenMap}
	)}

{CALL PEG_SMS.P2P_G03_APPR_NO(
		#{cust_id,		mode=IN,	jdbcType=VARCHAR,	javaType=java.lang.String},
		#{token,		mode=IN,	jdbcType=VARCHAR,	javaType=java.lang.String},
		#{request_id,	mode=IN,	jdbcType=VARCHAR,	javaType=java.lang.String},
		#{request_ip,	mode=IN,	jdbcType=VARCHAR,	javaType=java.lang.String},
		#{an_ret,		mode=OUT,	jdbcType=VARCHAR,	javaType=java.lang.String,	resultMap=tokenMap},
		#{an_dbcode,	mode=OUT,	jdbcType=INTEGER,	javaType=java.lang.Integer,	resultMap=tokenMap},
		#{as_dberrtext,	mode=OUT,	jdbcType=VARCHAR,	javaType=java.lang.String,	resultMap=tokenMap}
	)}

SELECT SMSNO, RESULT, /* 전송결과 코드 */ RES_DSC /* 전송결과 내용 */ FROM ( SELECT C.CUST_CD, M.CUST_NM, M.SNT_TELNO as CALLBACK, C.SMSNO, C.SNT_DTM, C.SNT_TELNO, C.SNT_CONT, C.RSV_YN, D.RECV_TELNO, D.RECV_NM, NVL(S.RESULT,0) AS RESULT, CD.DSC AS RES_DSC FROM TGWSM_M C JOIN TGWSM_CUST_INFO M on (C.CUST_CD = M.CUST_CD) JOIN TGWSMTGT D ON (C.SMSNO = D.SMSNO) LEFT OUTER JOIN (SELECT SR.RESERVED1, PHONE_NUMBER, RESULT FROM SDK_SMS_REPORT SR, SDK_SMS_REPORT_DETAIL SD WHERE SR.MSG_ID = SD.MSG_ID ) S ON D.SMSNO = S.RESERVED1 AND D.RECV_TELNO = S.PHONE_NUMBER LEFT OUTER JOIN P2T_CD_D CD ON (CD.CD_ID = '300' AND CD.CD = nvl(S.RESULT,0) ) WHERE C.SMSNO IN(
#{smsno}
) AND C.MMS_YN = 'N' /*SMS 조회*/ UNION ALL /*MMS 조회*/ SELECT C.CUST_CD, M.CUST_NM, M.SNT_TELNO as CALLBACK, C.SMSNO, C.SNT_DTM, C.SNT_TELNO, c.SNT_CONT, C.RSV_YN, D.RECV_TELNO, D.RECV_NM, NVL(S.RESULT,0) AS RESULT, CD.DSC AS RES_DSC FROM TGWSM_M C JOIN TGWSM_CUST_INFO M on (C.CUST_CD = M.CUST_CD) JOIN TGWSMTGT D ON (C.SMSNO = D.SMSNO) LEFT OUTER JOIN (SELECT SR.RESERVED1, PHONE_NUMBER, RESULT FROM SDK_MMS_REPORT SR, SDK_MMS_REPORT_DETAIL SD WHERE SR.MSG_ID = SD.MSG_ID ) S ON D.SMSNO = S.RESERVED1 AND D.RECV_TELNO = S.PHONE_NUMBER LEFT OUTER JOIN P2T_CD_D CD ON (CD.CD_ID = '300' AND CD.CD = nvl(S.RESULT,0) ) WHERE C.SMSNO IN(
#{smsno}
) AND C.MMS_YN = 'Y' ) GROUP BY SMSNO, RESULT, RES_DSC ORDER BY RES_DSC DESC
